import { NavLink } from "react-router-dom";
import logo from "../assets/logo.svg";

const linkBase =
  "flex items-center gap-2 px-4 py-2 rounded-xl hover:bg-slate-800";
const active = "bg-slate-800";

export default function Sidebar() {
  return (
    <aside className="w-64 bg-slate-950/60 border-r border-slate-800 p-4 space-y-6">
      <div className="flex items-center gap-3 px-2">
        <img src={logo} alt="InfraCheck" className="h-8 w-8" />
        <span className="font-semibold">InfraCheck</span>
      </div>

      <nav className="space-y-2">
        <NavLink to="/" end className={({isActive}) => `${linkBase} ${isActive ? active : ""}`}>
          <span>üè†</span> Home
        </NavLink>
        <NavLink to="/perfil" className={({isActive}) => `${linkBase} ${isActive ? active : ""}`}>
          <span>üë§</span> Perfil
        </NavLink>
      </nav>

      <div className="mt-auto">
        <div className="h-10 w-10 rounded-full bg-slate-700" />
        <p className="mt-2 text-sm opacity-70">Persona</p>
      </div>
    </aside>
  );
}
