export default function Profile() {
  // en el futuro puedes traer datos desde context o API
  const user = {
    nombre: "Ricardo Peña",
    email: "ricardo@example.com",
    rut: "11.111.111-1",
    direccion: "Av. Siempre Viva 123, Temuco",
  };

  return (
    <div className="max-w-3xl mx-auto">
      <h1 className="text-xl font-semibold mb-6">Perfil de Usuario</h1>

      <div className="rounded-2xl border border-slate-800 p-6 bg-slate-900/40">
        <div className="flex flex-col items-center gap-3">
          <div className="h-28 w-28 rounded-full border border-slate-700 bg-slate-800" />
          <h2 className="text-lg">{user.nombre}</h2>
        </div>

        <div className="mt-6 grid gap-3">
          <button className="rounded-xl bg-indigo-600/80 hover:bg-indigo-600 px-4 py-2 text-sm">
            Correo: {user.email}
          </button>
          <button className="rounded-xl bg-indigo-600/80 hover:bg-indigo-600 px-4 py-2 text-sm">
            RUT: {user.rut}
          </button>
          <button className="rounded-xl bg-indigo-600/80 hover:bg-indigo-600 px-4 py-2 text-sm">
            Dirección: {user.direccion}
          </button>
        </div>
      </div>
    </div>
  );
}
